import React from 'react';
import '../css/TodoManager.css'
import TodoActionBar from './TodoActionBar'
import ListContainer from './ListContainer'

class TodoManager extends React.Component {

    state = {
        todoItems: []
    };

    addItem = () => {
        let newTodoItems = this.state.todoItems.slice();
        let todoText = this.state.todoInputTextValue;
        let todoID = new Date().getTime();
        newTodoItems.push({todoID:todoID, todoText: todoText});
        this.setState({todoItems: newTodoItems, todoInputTextValue: ''});
    }

    onInputTextChange = (event) => {
        this.setState({todoInputTextValue:event.target.value});
    }

    render() {
        return (
            <div className='todo-manager'>
                < TodoActionBar 
                onInputTextChange={this.onInputTextChange}
                addItem={this.addItem}
                />
                < ListContainer
                todoItems={this.state.todoItems}
                 />
            </div>
        );
    }
}

export default TodoManager;
