import React from 'react';
const listTemplate = (props) => {
    let status = "list-item";
    if(props.status) {
        status = "list-item done-class"
    }
    if(props.checked) {
    return (
        <div className="list-item" todo-id={props.id}>
            <input type="checkbox" todo-type="check" className="check-box" checked></input>
            <p todo-type="text-holder">{props.text}</p>
            <button className="buttonClass-done"  todo-type="btn-done">Done</button>
            <button className="buttonClass-x" todo-type = "btn-x" onClick={props.buttonCloseClickHandler}>X</button>
        </div>
        );
    }
    return (
        <div className="list-item" todo-id={props.id}>
            <input type="checkbox" todo-type="check" className="check-box"></input>
            <p todo-type="text-holder">{props.text}</p>
            <button className="buttonClass-done"  todo-type="btn-done">Done</button>
            <button className="buttonClass-x" todo-type = "btn-x" onClick={props.buttonCloseClickHandler}>X</button>
        </div>
        );
}

export default listTemplate;
