import React, { Component } from 'react';
import './App.css';
import ListTemplate from './ListTemplate/ListTemplate.js';

class App extends Component {

  state = {
    listOfObjects: [
      {id: 1, text: "ONE" , status: false, checked: false}
    ],
    listItemElement: (<ListTemplate 
    item={this.state.listOfObjects[0]} 
    buttonCloseClickHandler={this.removeItem} ></ListTemplate>)
  }

  addItem = () => {
    let listContainer = document.getElementById('list-container');
  }

  removeItem = () => {

  } 

  addButtonHandler = () => {

  }

  textBoxOnEnterKeyHandler = (event) => {
      this.setState({listOfObjects: [
        {id: 1, text: event.target.value , status: false, checked: true}
      ]
    })
  }


  render() {
    return (
      <div className="App">

        <div id="text-input">
            <input type="text" id="text-box" placeholder="Write something to add" onKeyPress={this.textBoxOnEnterKeyHandler}></input>
            <button id="btn-add" className="input-button"onClick={this.addButtonHandler}>Add</button>
            <button id="btn-select-all" className="input-button">Select/Deselect All</button>
            <button id="btn-delete-selected" className="input-button">Delete Selected</button>
            <button id="btn-delete-completed" className="input-button">Delete Completed</button>
        </div>

        <div id="list-container">
            {this.state.listItemElement}
        </div>
      </div>
    );
  }
}

export default App;
