    if (event.which === 13) {
      if (textValue) {
        this.setState({
          listOfObjects: event.target.value
        })
      }
    }
  }


  render() {
    return ( <
      div className = "App" >

      <
      div id = "text-input" >
      <
      input type = "text"
      id = "text-box"
      placeholder = "Write something to add"
      onKeyPress = {
        this.textBoxOnEnterKeyHandler
      } > < /input> <
      button id = "btn-add"
      className = "input-button"
      onClick = {
        this.addButtonHandler
      } > Add < /button> <
      button id = "btn-select-all"
      className = "input-button" > Select / Deselect All < /button> <
      button id = "btn-delete-selected"
      className = "input-button" > Delete Selected < /button> <
      button id = "btn-delete-completed"
      className = "input-button" > Delete Completed < /button> <
      /div>

      <
      div id = "list-container" >
      <
      ListTemplate text = {
        this.state.listOfObjects
      }
      buttonCloseClickHandler = {
        this.removeItem
      } > < /ListTemplate> <
      /div> <
      /div>
    );
  }
}

export default App;
